import "./share.scss";
import Friend from "../../assets/friends.jpeg";
import { useContext } from "react";
import { AuthContext } from "../../context/authContext";

const Share = () => {
    const [file, setFile] = useState(null)
    const [desc, setDesc] = useState("")

    const { currentUser } = useContext(AuthContext)
    return (
        <div className="share">
            <div className="container">
                <div className="top">
                    <img
                        src={currentUser.profilePic}
                        alt=""
                    />
                    <input type="text" placeholder={`What's on your mind ${currentUser.name}?`}
                        onChange={e => setDesc(e.target.val)}
                    />
                </div>
                <hr />
                <div className="bottom">
                    <div className="left">
                        <input type="file" id="file" style={{ display: "none" }} onChange={e => setDesc(e.target.val)} />
                        <label htmlFor="file">
                            <div className="item">
                                <img src={Friend} alt="" />
                                <span>Add Image</span>
                            </div>
                        </label>
                        <div className="item">
                            <img src={Friend} alt="" />
                            <span>Add Place</span>
                        </div>
                        <div className="item">
                            <img src={Friend} alt="" />
                            <span>Tag Friends</span>
                        </div>
                    </div>
                    <div className="right">
                        <button>Share</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Share;
